html{
    max-width: 100%;
    overflow-x: hidden;
}
*{
    margin: 0;
}
body{
    margin: 0;
    position: relative;
    background: $beige;
    @media only screen and (max-width: $breakpoint) {
        overflow-x: hidden;
    }
}

.break{
    height: 250px;
    @media only screen and (max-width: $breakpointmobile) {
        height: 150px;
    }
}
.divider{
    width: 100px;
    height: 25px;
    background-color: black;
    margin-bottom: 40px;
    margin-left: 20px;
    @media only screen and (max-width: $breakpointmobile) {
        width: 60px;
        height: 15px;
        margin-bottom: 30px;
    }
}

a{
    text-decoration: none;
    color: black;
    transition: 0.1s;
}
a:hover{
    color: transparent;
    -webkit-text-stroke-width: 0.5px;
    -webkit-text-stroke-color: $red;
}

