sources.styles/title.scss:
h1 {
    font-family: "Akira";
    font-size: 230px;
    line-height: 76.5%;
    font-weight: 100;
    @media only screen and (max-width: $breakpoint) {
        font-size: 200px;
    }
    @media only screen and (max-width: $breakpointmobile) {
        font-size: 100px;
    }
    @media only screen and (min-width: $breakpointmonitor) {
        font-size: 300px;
    }
}

.title{
    // margin: 20px;
    margin: 20px 20px 0 20px;
}
.title_image1{
    height: 150px;
    margin-bottom: 2px;
    display: inline-block;
    @media only screen and (max-width: $breakpoint) {
        height: 130px;
    }
    @media only screen and (max-width: $breakpointmobile) {
        height: 70px;
        margin-bottom: 0px;
    }

    @media only screen and (min-width: $breakpointmonitor) {
        height: 200px;
    }
}
.title_image2{
    height: 130px;
    margin-bottom: 2px;
    display: inline-block;
    @media only screen and (max-width: $breakpoint) {
        height: 120px;
    }
    @media only screen and (max-width: $breakpointmobile) {
        height: 65px;
        margin-bottom: 0px;
    }

    @media only screen and (min-width: $breakpointmonitor) {
        height: 180px;
    }
}

sources.styles/contents.scss:
.contents_container{
    margin-top: 80px;
    margin-left: 20px;
    margin-right: 20px;
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 50px;
    @media only screen and (max-width: $breakpointmobile) {
        grid-template-columns: 1fr;
        gap: 40px;
    }
}
.contents_title{
    margin-left:20px;
}
.contents_button_container{
    font-family: "Akira";
    display: grid;
    grid-template-columns: 1fr 5fr;
    width: 40vw;
    grid-gap: 30px;
    transition: 0.1s;
    cursor: pointer;
    width: 100%;
}
.contents_button_container:hover {
    color: transparent;
    -webkit-text-stroke-width: 1px;
  -webkit-text-stroke-color: $red;
  }
.contents_button_label{
    font-size: 70px;
    line-height: 80%;
    margin-top: 2px;
    @media only screen and (max-width: $breakpoint) {
        font-size: 50px;
    }
}
.contents_button_title{
    font-size: 70px;
    line-height: 80%;
    @media only screen and (max-width: $breakpoint) {
        font-size: 50px;
    }
}

sources.styles/essay.scss:
.essay_container{
    display: grid;
    grid-template-columns: 1fr 3fr 1fr;
    grid-template-rows: auto auto auto auto;
    column-gap: 40px;
    margin: 0 20px 0 20px;


}
@media only screen and (max-width: $breakpoint) {
    .essay_container{

    }
}

//Title
.essay_title_container{
    grid-column-start: 1;
    grid-column-end: 4;
    grid-row-start: 1;
    grid-row-end: 100;
    margin-bottom: 60px;
    font-size: 40px;
    z-index: 10;
}

.essay_title{
    position: -webkit-sticky;
    position: sticky;
    top: 20px;
    transition: 0.4s;
    overflow: visible;
    @media only screen and (max-width: $breakpoint) {
        display: none;
    }
}
.mobile_essay_title{
    display: none;
}

    @media only screen and (max-width: $breakpoint) {
        .title_break{
            display: none;
        }
        .mobile_essay_title{
            font-family: "Akira";
            font-size: 200px;
            line-height: 76.5%;
            font-weight: 100;
            display: block;
            margin-bottom: 60px;
        }
    }
    @media only screen and (max-width: $breakpointmobile) {
        .title_break{
            display: none;
        }
        .mobile_essay_title{
            font-family: "Akira";
            font-size: 100px;
            line-height: 76.5%;
            font-weight: 100;
            display: block;
            margin-bottom: 60px;
        }
    }

//Paragraph
.essay_body{
    grid-column-start: 2;
    grid-column-end: 3;
    grid-row-start: 1;
    grid-row-end: 2;
    margin-top: 410px;
    font-family: Authentic;
    font-size: 19px;
    line-height: 158%;
}
@media only screen and (max-width: $breakpoint) {
    .essay_body{
        grid-column-start: 1;
        grid-column-end: 4;
        margin-top: 0;
    }
}

.footnote{
    margin-left: 2px;
    margin-right: 2px;
    font-family: Akira;
    font-size: 21px;
    position:relative;
    top:-3px;
}

//Footnotes
.footnote_container{
    margin-top: 410px;
    grid-column-start: 3;
    grid-column-end: 4;
    grid-row-start: 1;
    grid-row-end: 100;
    z-index: 100;
    @media only screen and (max-width: $breakpoint) {
        margin-top: 60px;
        grid-column-start: 1;
        grid-column-end: 4;
        grid-row-start: 3;
        grid-row-end: 4;
        width: 100%;
        display: grid;
        grid-template-columns: 1fr 1fr 1fr;
        gap: 20px;
    }
    @media only screen and (max-width: $breakpointmobile) {
        margin-top: 60px;
        grid-column-start: 1;
        grid-column-end: 4;
        grid-row-start: 3;
        grid-row-end: 4;
        width: 100%;
        display: grid;
        grid-template-columns: 1fr 1fr;
        gap: 20px;
    }
}
.threeliner{
    margin-top: 610px;
    @media only screen and (max-width: $breakpoint) {
        margin-top: 0;
    }
}
.footnote_container_inner{
    margin-bottom: 40px;
}
.footnote_gap{
    margin-bottom: 10px;
}
h4{
    font-family: Akira;
    font-weight: normal;
    font-size: 40px;
}
.footnote_body{
    font-family: Authentic;
    font-size: 13px;
    line-height: 150%;
}

//Image Basic
$imagebasicwidth: 660px;

.image_basic_container{
    grid-column-start: 1;
    grid-column-end: 4;
    grid-row-start: 2;
    grid-row-end: 3;
    margin-top: 20px;
    margin-bottom: 40px;
    display: flex;
    flex-direction: column;
    gap: 20px;
    transition: 0.4s;
    @media only screen and (max-width: $breakpoint) {
        margin-top: 40px;
    }
}

.image_figure{
}
.image_image{
    width: $imagebasicwidth;
}
.image_description{
    font-family: Ortica;
    font-size: 34px;
    line-height: 130%;
    width: $imagebasicwidth;
    max-width: $imagebasicwidth;
    @media only screen and (max-width: $breakpoint) {
        font-size: 27px;
    }
}
@media only screen and (max-width: $breakpoint) {
    .image_image{
        width: 100%;
    }
    .image_description{
        width: 100%;
    }
}

//Pullquote
.pullquote{
    z-index: 10;
    grid-column-start: 1;
    grid-column-end: 4;
    grid-row-start: 2;
    grid-row-end: 3;
    font-family: Ortica;
    font-weight: 100;
    line-height: 130%;
    font-size: 34px;
    max-width: $imagebasicwidth;
    margin: 40px 0 40px 0;
    transition: 0.4s;
}
.image_basic_container_translate{
    transform: translate(21vw);
    @media only screen and (min-width: $breakpointmonitor) {
        transform: translate(20.5vw);
    }
}

//ROWS
.row1{
    grid-row-start: 2;
    grid-row-end: 3;
}
.row2{
    grid-row-start: 3;
    grid-row-end: 4;
    margin-top: 0;
}
.row3{
    grid-row-start: 4;
    grid-row-end: 5;
    margin-top: 0;
}
.row4{
    grid-row-start: 5;
    grid-row-end: 6;
    margin-top: 0;
}
.row5{
    grid-row-start: 6;
    grid-row-end: 7;
    margin-top: 0;
}
.row6{
    grid-row-start: 7;
    grid-row-end: 8;
    margin-top: 0;
}

.row4_mobile{
    @media only screen and (max-width: $breakpoint) {
        grid-row-start: 4;
        grid-row-end: 5;
    }
}
.row5_mobile{
    @media only screen and (max-width: $breakpoint) {
        grid-row-start: 5;
        grid-row-end: 6;
    }
}
.row6_mobile{
    @media only screen and (max-width: $breakpoint) {
        grid-row-start: 6;
        grid-row-end: 7;
    }
}
.row7_mobile{
    @media only screen and (max-width: $breakpoint) {
        grid-row-start: 7;
        grid-row-end: 8;
    }
}
.row8_mobile{
    @media only screen and (max-width: $breakpoint) {
        grid-row-start: 8;
        grid-row-end: 9;
    }
}

.footnote_margin_mobile{
    margin-top: 100px;
}
.image_margin_mobile{
    margin-top: 40px;
    margin-bottom: 40px;
}

.parenthesis{
    font-size: 200px;
    position:relative;
    top:-8px;
    transition: 0.4s;
}

//BACK TOP
.back_top{
    margin-top: 30px;
    cursor: pointer;
}
.back_top:hover {
    color: transparent;
    -webkit-text-stroke-width: 1px;
  -webkit-text-stroke-color: $red;
  }

sources.styles/carousel.scss:
.carousel_container{
    display: flex;
    width: 100vw;
    margin: 60px 0 60px 0;
    @media only screen and (max-width: $breakpoint) {
        margin: 40px 0 40px 0;
    }
}
.carousel_image{
    width: 33vw;
    @media only screen and (max-width: $breakpointmobile) {
        width: 100%;
    }
}

.carousel_description_container{
    display: flex;
    gap: 60px;
    margin-left: 21vw;
    @media only screen and (max-width: $breakpointmobile) {
        flex-direction: column;
        margin-left: 20px;
        margin-right: 20px;
        gap: 20px;
    }
}
.carousel_description_fig{
    font-family: Akira;
    font-size: 40px;
    width: auto;
}
.carousel_description_caption{
    font-family: Ortica;
    font-weight: 100;
    line-height: 130%;
    font-size: 34px;
    @media only screen and (max-width: $breakpoint) {
        font-size: 27px;
    }
}

.hide_mobile{
    @media only screen and (max-width: $breakpointmobile) {
        display: none;
    }
}

sources.styles/style.scss:
$breakpointmobile: 800px;
$breakpoint: 1300px;
$breakpointmonitor: 1900px;

//Fonts
@import 'fonts', 'colors';

//Sections
@import 'title', 'contents', 'essay', 'carousel';

html{
    max-width: 100%;
    overflow-x: hidden;
}
*{
    margin: 0;
}
body{
    margin: 0;
    position: relative;
    background: $beige;
    @media only screen and (max-width: $breakpoint) {
        overflow-x: hidden;
    }
}

.break{
    height: 250px;
    @media only screen and (max-width: $breakpointmobile) {
        height: 150px;
    }
}
.divider{
    width: 100px;
    height: 25px;
    background-color: black;
    margin-bottom: 40px;
    margin-left: 20px;
    @media only screen and (max-width: $breakpointmobile) {
        width: 60px;
        height: 15px;
        margin-bottom: 30px;
    }
}

a{
    text-decoration: none;
    color: black;
    transition: 0.1s;
}
a:hover{
    color: transparent;
    -webkit-text-stroke-width: 0.5px;
    -webkit-text-stroke-color: $red;
}

body {
    margin: 0;
    padding: 0;
  }






  .header {
    position: fixed;
    bottom: 0;
    z-index: 100;
    width: 100%;
  }

  .progress-container {
    width: 100%;
    height: 10px;
    background: transparent;
  }

  .progress-bar {
    height: 10px;
    background: rgb(238, 26, 26);
    width: 0%;
  }
